import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { 
  FaMapMarkerAlt, 
  FaCompass, 
  FaCamera, 
  FaUtensils, 
  FaHotel, 
  FaCar, 
  FaClock, 
  FaDollarSign,
  FaStar,
  FaHeart,
  FaShare,
  FaDownload,
  FaFilter,
  FaSearch,
  FaSignOutAlt,
  FaUser,
  FaBell,
  FaLocationArrow,
  FaEdit,
  FaPlane,
  FaHeart as FaWishlist
} from 'react-icons/fa';
import { locationService } from './LocationService';
import EditProfile from './EditProfile';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [userLocation, setUserLocation] = useState(null);
  const [locationName, setLocationName] = useState(null);
  const [selectedDay, setSelectedDay] = useState(0);
  const [favorites, setFavorites] = useState(new Set());
  const [filterType, setFilterType] = useState('all');
  const [showLocationDetails, setShowLocationDetails] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [userData, setUserData] = useState({
    name: '',
    age: '',
    gender: '',
    contact: '',
    emergencyNumber: '',
    emergencyRelation: '',
    traveledLocations: [],
    wishlist: [],
    activities: []
  });

  // Mock itinerary data
  const itineraryData = {
    location: "Paris, France",
    duration: "5 days",
    budget: "$2,500",
    itinerary: [
      {
        day: 1,
        date: "2024-01-15",
        title: "Arrival & City Introduction",
        activities: [
          {
            id: 1,
            time: "10:00 AM",
            title: "Check-in at Hotel",
            location: "Hotel des Invalides",
            type: "accommodation",
            duration: "1 hour",
            price: "$150/night",
            rating: 4.8,
            description: "Luxury hotel in the heart of Paris with stunning city views",
            image: "https://images.unsplash.com/photo-1566073771259-6a8506099945?ixlib=rb-4.0.3&auto=format&fit=crop&w=1000&q=80"
          },
          {
            id: 2,
            time: "12:00 PM",
            title: "Lunch at Local Bistro",
            location: "Café de Flore",
            type: "dining",
            duration: "1.5 hours",
            price: "$45",
            rating: 4.6,
            description: "Traditional French cuisine in a historic café",
            image: "https://images.unsplash.com/photo-1551218808-94e220e084d2?ixlib=rb-4.0.3&auto=format&fit=crop&w=1000&q=80"
          },
          {
            id: 3,
            time: "2:00 PM",
            title: "Eiffel Tower Visit",
            location: "Champ de Mars",
            type: "attraction",
            duration: "3 hours",
            price: "$25",
            rating: 4.9,
            description: "Iconic landmark with panoramic city views",
            image: "https://images.unsplash.com/photo-1511739001486-6bfe10ce785f?ixlib=rb-4.0.3&auto=format&fit=crop&w=1000&q=80"
          }
        ]
      },
      {
        day: 2,
        date: "2024-01-16",
        title: "Art & Culture Day",
        activities: [
          {
            id: 4,
            time: "9:00 AM",
            title: "Louvre Museum",
            location: "Rue de Rivoli",
            type: "attraction",
            duration: "4 hours",
            price: "$20",
            rating: 4.7,
            description: "World's largest art museum and historic monument",
            image: "https://images.unsplash.com/photo-1541961017774-22349e4a1262?ixlib=rb-4.0.3&auto=format&fit=crop&w=1000&q=80"
          },
          {
            id: 5,
            time: "2:00 PM",
            title: "Seine River Cruise",
            location: "Port de la Bourdonnais",
            type: "activity",
            duration: "1 hour",
            price: "$35",
            rating: 4.5,
            description: "Scenic boat tour along the Seine River",
            image: "https://images.unsplash.com/photo-1502602898536-47ad22581b52?ixlib=rb-4.0.3&auto=format&fit=crop&w=1000&q=80"
          }
        ]
      },
      {
        day: 3,
        date: "2024-01-17",
        title: "Montmartre & Local Life",
        activities: [
          {
            id: 6,
            time: "10:00 AM",
            title: "Sacré-Cœur Basilica",
            location: "Montmartre",
            type: "attraction",
            duration: "2 hours",
            price: "Free",
            rating: 4.8,
            description: "Beautiful basilica with stunning city views",
            image: "https://images.unsplash.com/photo-1520637836862-4d197d17c0a4?ixlib=rb-4.0.3&auto=format&fit=crop&w=1000&q=80"
          },
          {
            id: 7,
            time: "1:00 PM",
            title: "Local Market Tour",
            location: "Marché des Enfants Rouges",
            type: "activity",
            duration: "2 hours",
            price: "$30",
            rating: 4.4,
            description: "Explore local food markets and taste authentic French delicacies",
            image: "https://images.unsplash.com/photo-1556909114-f6e7ad7d3136?ixlib=rb-4.0.3&auto=format&fit=crop&w=1000&q=80"
          }
        ]
      }
    ]
  };

  useEffect(() => {
    // Get user location using our location service
    const getLocation = async () => {
      try {
        const location = await locationService.getCurrentLocation();
        setUserLocation({
          lat: location.latitude,
          lng: location.longitude,
          accuracy: location.accuracy
        });

        // Get location name
        const locationNameData = await locationService.getLocationName(
          location.latitude, 
          location.longitude
        );
        setLocationName(locationNameData);
        console.log('Current location:', locationNameData.fullName);
      } catch (error) {
        console.log('Location access denied or unavailable:', error.message);
        // Fallback to default location for demo
        setUserLocation({
          lat: 48.8566,
          lng: 2.3522,
          accuracy: 0
        });
        setLocationName({
          city: 'Paris',
          country: 'France',
          region: 'Île-de-France',
          fullName: 'Paris, France'
        });
      }
    };

    getLocation();
  }, []);

  const toggleFavorite = (activityId) => {
    const newFavorites = new Set(favorites);
    if (newFavorites.has(activityId)) {
      newFavorites.delete(activityId);
    } else {
      newFavorites.add(activityId);
    }
    setFavorites(newFavorites);
  };

  const getActivityIcon = (type) => {
    switch (type) {
      case 'accommodation': return <FaHotel />;
      case 'dining': return <FaUtensils />;
      case 'attraction': return <FaCamera />;
      case 'activity': return <FaCompass />;
      case 'transport': return <FaCar />;
      default: return <FaMapMarkerAlt />;
    }
  };

  const filteredActivities = filterType === 'all' 
    ? itineraryData.itinerary[selectedDay].activities
    : itineraryData.itinerary[selectedDay].activities.filter(activity => activity.type === filterType);

  return (
    <div className="dashboard">
      {/* Header */}
      <motion.header 
        className="dashboard-header"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        <div className="header-content">
          <div className="header-left">
            <motion.div 
              className="logo"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FaCompass className="logo-icon" />
              <span>Wanderly</span>
            </motion.div>
            <div className="location-info">
              <FaMapMarkerAlt className="location-icon" />
              <motion.div 
                className="location-name-container"
                onClick={() => userLocation && setShowLocationDetails(!showLocationDetails)}
                whileHover={userLocation ? { scale: 1.02 } : {}}
                whileTap={userLocation ? { scale: 0.98 } : {}}
                style={{ cursor: userLocation ? 'pointer' : 'default' }}
              >
                <span className="location-name">
                  {locationName ? locationName.fullName : itineraryData.location}
                </span>
                {userLocation && (
                  <div className="location-indicator">
                    <FaLocationArrow className="indicator-icon" />
                    <span className="indicator-text">Click for details</span>
                  </div>
                )}
              </motion.div>
            </div>
          </div>
          <div className="header-right">
            <button className="header-btn">
              <FaBell />
            </button>
            <button className="header-btn">
              <FaUser />
            </button>
            <button 
              className="logout-btn"
              onClick={() => navigate('/')}
            >
              <FaSignOutAlt />
            </button>
          </div>
        </div>
      </motion.header>

      {/* Location Details Panel */}
      <AnimatePresence>
        {showLocationDetails && userLocation && (
          <motion.div 
            className="location-details-panel"
            initial={{ opacity: 0, y: -20, height: 0 }}
            animate={{ opacity: 1, y: 0, height: 'auto' }}
            exit={{ opacity: 0, y: -20, height: 0 }}
            transition={{ duration: 0.3 }}
          >
            <div className="location-panel-content">
              <div className="location-panel-header">
                <h3>Your Current Location</h3>
                <button 
                  className="close-btn"
                  onClick={() => setShowLocationDetails(false)}
                >
                  ×
                </button>
              </div>

              <div className="location-data-grid">
                <div className="location-data-item">
                  <div className="data-label">
                    <FaMapMarkerAlt />
                    <span>Coordinates</span>
                  </div>
                  <div className="data-value">
                    <div className="coordinate">
                      <strong>Latitude:</strong> {userLocation.lat.toFixed(6)}
                    </div>
                    <div className="coordinate">
                      <strong>Longitude:</strong> {userLocation.lng.toFixed(6)}
                    </div>
                  </div>
                </div>

                <div className="location-data-item">
                  <div className="data-label">
                    <FaCompass />
                    <span>Location Details</span>
                  </div>
                  <div className="data-value">
                    <div className="location-detail">
                      <strong>City:</strong> {locationName?.city || 'Unknown'}
                    </div>
                    <div className="location-detail">
                      <strong>Region:</strong> {locationName?.region || 'Unknown'}
                    </div>
                    <div className="location-detail">
                      <strong>Country:</strong> {locationName?.country || 'Unknown'}
                    </div>
                  </div>
                </div>

                <div className="location-data-item">
                  <div className="data-label">
                    <FaLocationArrow />
                    <span>Accuracy</span>
                  </div>
                  <div className="data-value">
                    <div className="accuracy-info">
                      <div className="accuracy-value">
                        {userLocation.accuracy > 0 ? `${Math.round(userLocation.accuracy)}m` : 'Unknown'}
                      </div>
                      <div className="accuracy-bar">
                        <div 
                          className="accuracy-fill"
                          style={{ 
                            width: `${Math.min(100, Math.max(0, 100 - (userLocation.accuracy / 100)))}%` 
                          }}
                        ></div>
                      </div>
                    </div>
                  </div>
                </div>

                <div className="location-data-item">
                  <div className="data-label">
                    <FaClock />
                    <span>Detection Time</span>
                  </div>
                  <div className="data-value">
                    <div className="timestamp">
                      {new Date().toLocaleString()}
                    </div>
                  </div>
                </div>
              </div>

              <div className="location-actions">
                <button 
                  className="action-btn"
                  onClick={() => {
                    const mapsUrl = `https://www.google.com/maps?q=${userLocation.lat},${userLocation.lng}`;
                    window.open(mapsUrl, '_blank');
                  }}
                >
                  <FaMapMarkerAlt />
                  Open in Maps
                </button>
                <button 
                  className="action-btn"
                  onClick={() => {
                    const shareText = `I'm currently at ${locationName?.fullName || 'Unknown Location'} (${userLocation.lat.toFixed(6)}, ${userLocation.lng.toFixed(6)})`;
                    if (navigator.share) {
                      navigator.share({
                        title: 'My Location',
                        text: shareText,
                        url: `https://www.google.com/maps?q=${userLocation.lat},${userLocation.lng}`
                      });
                    } else {
                      navigator.clipboard.writeText(shareText);
                      alert('Location copied to clipboard!');
                    }
                  }}
                >
                  <FaShare />
                  Share Location
                </button>
                <button 
                  className="action-btn"
                  onClick={async () => {
                    try {
                      const location = await locationService.getCurrentLocation();
                      setUserLocation({
                        lat: location.latitude,
                        lng: location.longitude,
                        accuracy: location.accuracy
                      });

                      const locationNameData = await locationService.getLocationName(
                        location.latitude, 
                        location.longitude
                      );
                      setLocationName(locationNameData);
                    } catch (error) {
                      console.log('Error refreshing location:', error.message);
                    }
                  }}
                >
                  <FaCompass />
                  Refresh Location
                </button>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Main Content */}
      <div className="dashboard-content">
        {/* Sidebar */}
        <motion.aside 
          className="sidebar"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
        >
          <div className="trip-summary">
            <h3>Trip Summary</h3>
            <div className="summary-item">
              <FaClock />
              <span>{itineraryData.duration}</span>
            </div>
            <div className="summary-item">
              <FaDollarSign />
              <span>{itineraryData.budget}</span>
            </div>
            <div className="summary-item">
              <FaMapMarkerAlt />
              <span>{itineraryData.location}</span>
            </div>
          </div>

          <div className="day-selector">
            <h3>Itinerary Days</h3>
            {itineraryData.itinerary.map((day, index) => (
              <motion.button
                key={day.day}
                className={`day-btn ${selectedDay === index ? 'active' : ''}`}
                onClick={() => setSelectedDay(index)}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                <div className="day-number">Day {day.day}</div>
                <div className="day-date">{day.date}</div>
                <div className="day-title">{day.title}</div>
              </motion.button>
            ))}
          </div>

          <div className="action-buttons">
            <button className="action-btn">
              <FaDownload />
              Download Itinerary
            </button>
            <button className="action-btn">
              <FaShare />
              Share Trip
            </button>
          </div>
        </motion.aside>

        {/* Main Itinerary */}
        <motion.main 
          className="main-content"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.4 }}
        >
          {/* Day Header */}
          <div className="day-header">
            <h1>{itineraryData.itinerary[selectedDay].title}</h1>
            <p>Day {itineraryData.itinerary[selectedDay].day} • {itineraryData.itinerary[selectedDay].date}</p>
          </div>

          {/* Filters */}
          <div className="filters">
            <div className="filter-group">
              <FaFilter />
              <select 
                value={filterType} 
                onChange={(e) => setFilterType(e.target.value)}
                className="filter-select"
              >
                <option value="all">All Activities</option>
                <option value="attraction">Attractions</option>
                <option value="dining">Dining</option>
                <option value="accommodation">Accommodation</option>
                <option value="activity">Activities</option>
                <option value="transport">Transport</option>
              </select>
            </div>
            <div className="search-box">
              <FaSearch />
              <input type="text" placeholder="Search activities..." />
            </div>
          </div>

          {/* Activities List */}
          <div className="activities-container">
            <AnimatePresence mode="wait">
              <motion.div
                key={selectedDay}
                className="activities-list"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 }}
              >
                {filteredActivities.map((activity, index) => (
                  <motion.div
                    key={activity.id}
                    className="activity-card"
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.4, delay: index * 0.1 }}
                    whileHover={{ scale: 1.02, boxShadow: "0 10px 30px rgba(0,0,0,0.1)" }}
                  >
                    <div className="activity-image">
                      <img src={activity.image} alt={activity.title} />
                      <div className="activity-type">
                        {getActivityIcon(activity.type)}
                      </div>
                      <button 
                        className={`favorite-btn ${favorites.has(activity.id) ? 'active' : ''}`}
                        onClick={() => toggleFavorite(activity.id)}
                      >
                        <FaHeart />
                      </button>
                    </div>

                    <div className="activity-content">
                      <div className="activity-header">
                        <h3>{activity.title}</h3>
                        <div className="activity-rating">
                          <FaStar />
                          <span>{activity.rating}</span>
                        </div>
                      </div>

                      <div className="activity-details">
                        <div className="detail-item">
                          <FaClock />
                          <span>{activity.time}</span>
                        </div>
                        <div className="detail-item">
                          <FaMapMarkerAlt />
                          <span>{activity.location}</span>
                        </div>
                        <div className="detail-item">
                          <FaDollarSign />
                          <span>{activity.price}</span>
                        </div>
                        <div className="detail-item">
                          <FaClock />
                          <span>{activity.duration}</span>
                        </div>
                      </div>

                      <p className="activity-description">{activity.description}</p>

                      <div className="activity-actions">
                        <button className="btn-primary">View Details</button>
                        <button className="btn-secondary">Add to Favorites</button>
                      </div>
                    </div>
                  </motion.div>
                ))}
              </motion.div>
            </AnimatePresence>
          </div>
        </motion.main>
      </div>
    </div>
  );
};

export default Dashboard;
